import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AllPages.css';
import { GenericTable, GenericPopup } from './ReusableReactComponents';

const Popup = ({ title, children, onClose }) => (
  <div className="popup">
    <div className="popup-inner">
      <h3>{title}</h3>
      {children}
      <button className="close-btn" onClick={onClose}>Close</button>
    </div>
  </div>
);

const MenuManagement = () => {
    const [items, setItems] = useState([]);
    const [farms, setFarms] = useState([]);
    const [itemForm, setItemForm] = useState({ itemName: '', quantityAvailable: '', farm: '' });
    const [farmForm, setFarmForm] = useState({ name: '', address: '', contactNumber: '' });
    const [isItemModalOpen, setIsItemModalOpen] = useState(false);
    const [isFarmModalOpen, setIsFarmModalOpen] = useState(false);
    const [currentItemId, setCurrentItemId] = useState(null); // null for add, ID for edit
    const [showGenericPopup, setShowGenericPopup] = useState(false);
    const [popupContent, setPopupContent] = useState(null);

        // Function to handle opening the generic popup with dynamic content
    const openGenericPopup = (content) => {
        setPopupContent(content);
        setShowGenericPopup(true);
    };

    // Function to handle closing the generic popup
    const closeGenericPopup = () => {
        setShowGenericPopup(false);
        setPopupContent(null);
    };

    // Example usage for adding an item
    const handleAddItemOpen = () => {
        const content = (
            <form onSubmit={handleSubmitItem}>
                {/* Form fields */}
                <button type="submit">Save Item</button>
            </form>
        );
        openGenericPopup(content);
    };

    // Example usage for editing an item
    const handleEditItemOpen = (item) => {
        setCurrentItem(item);
        const content = (
            <form onSubmit={(e) => handleEditItemSubmit(e, item)}>
                {/* Form fields, pre-filled with item data */}
                <button type="submit">Update Item</button>
            </form>
        );
        openGenericPopup(content);
    };

    // Example usage for delete confirmation
    const handleDeleteConfirmation = (itemId) => {
        const content = (
            <div>
                <p>Are you sure you want to delete this item?</p>
                <button onClick={() => handleDeleteItem(itemId)}>Confirm Delete</button>
            </div>
        );
        openGenericPopup(content);
    };

    useEffect(() => {
        fetchItems();
        fetchFarms();
    }, []);

    const fetchItems = async () => {
        const response = await axios.get('/api/items');
        setItems(response.data);
    };

    const fetchFarms = async () => {
        const response = await axios.get('/api/farms');
        setFarms(response.data);
    };

    const handleSubmitItem = async (e) => {
        e.preventDefault();
        const method = currentItemId ? axios.put : axios.post;
        const url = currentItemId ? `/api/items/${currentItemId}` : '/api/items';

        try {
            await method(url, itemForm);
            resetItemForm();
            setIsItemModalOpen(false);
            fetchItems();
        } catch (error) {
            console.error('Error saving item:', error);
        }
    };

    const handleAddEditItem = (item = null) => {
        if (item) {
            setCurrentItemId(item._id);
            setItemForm({ itemName: item.itemName, quantityAvailable: item.quantityAvailable, farm: item.farm?._id || '' });
        } else {
            resetItemForm();
            setCurrentItemId(null);
        }
        setIsItemModalOpen(true);
    };

    const handleDeleteItem = async (itemId) => {
        try {
            await axios.delete(`/api/items/${itemId}`);
            fetchItems();
        } catch (error) {
            console.error('Failed to delete item:', error);
        }
    };

    const handleAddFarm = async (e) => {
        e.preventDefault();
        try {
            await axios.post('/api/farms', farmForm);
            resetFarmForm();
            setIsFarmModalOpen(false);
            fetchFarms();
        } catch (error) {
            console.error('Failed to add farm:', error);
        }
    };

    const resetItemForm = () => setItemForm({ itemName: '', quantityAvailable: '', farm: '' });
    const resetFarmForm = () => setFarmForm({ name: '', address: '', contactNumber: '' });

    return (
        <div className="menu-management-container">
            <div className="control-panel">
                <button onClick={() => handleAddEditItem()} className="add-button">
                    Add Menu Item
                </button>
                <button onClick={() => setIsFarmModalOpen(true)} className="add-button">
                    Add Farm
                </button>
            </div>
            <div className="menu-items-table">
                <h3>Menu Items</h3>
                <table>
                    <thead>
                        <tr>
                            <th>Item Name</th>
                            <th>Farm</th>
                            <th>Quantity Available</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {items.map(item => (
                            <tr key={item._id}>
                                <td>{item.itemName}</td>
                                <td>{item.farm ? item.farm.name : 'N/A'}</td>
                                <td>{item.quantityAvailable}</td>
                                <td>
                                    <button onClick={() => handleAddEditItem(item)} className="edit-btn">Edit</button>
                                    <button onClick={() => handleDeleteItem(item._id)} className="delete-btn">Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            {isItemModalOpen && (
                <Popup title={currentItemId ? "Edit Item" : "Add Item"} onClose={() => setIsItemModalOpen(false)}>
                    <form onSubmit={handleSubmitItem}>
                        {/* Item form inputs */}
                        <button type="submit">{currentItemId ? "Update Item" : "Add Item"}</button>
                    </form>
                </Popup>
            )}
            {isFarmModalOpen && (
                <Popup title="Add Farm" onClose={() => setIsFarmModalOpen(false)}>
                    <form onSubmit={handleAddFarm}>
                        {/* Farm form inputs */}
                        <button type="submit">Add Farm</button>
                    </form>
                </Popup>
            )}
        </div>
    );
};
export default MenuManagement;
