import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AllPages.css';
import { GenericTable, GenericPopup } from './ReusableReactComponents';

const ItemForm = ({ item, onSave, farms }) => {
  // Initialize form state with item data if editing, or blank for adding
  const [formState, setFormState] = useState({
    itemName: item ? item.itemName : '',
    quantityAvailable: item ? item.quantityAvailable : '',
    farm: item && item.farm ? item.farm._id : ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormState(prevState => ({ ...prevState, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // API call to add or update item
    try {
      if (item) {
        await axios.put(`/api/items/${item._id}`, formState);
      } else {
        await axios.post('/api/items', formState);
      }
      onSave(); // Trigger fetching items and closing popup
    } catch (error) {
      console.error('Failed to save item:', error);
      // Handle error (show message to user, etc.)
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="itemName">Item Name:</label>
      <input
        id="itemName"
        name="itemName"
        type="text"
        value={formState.itemName}
        onChange={handleChange}
        required
      />

      <label htmlFor="quantityAvailable">Quantity Available:</label>
      <input
        id="quantityAvailable"
        name="quantityAvailable"
        type="number"
        value={formState.quantityAvailable}
        onChange={handleChange}
        required
      />

      <label htmlFor="farm">Farm:</label>
      <select
        id="farm"
        name="farm"
        value={formState.farm}
        onChange={handleChange}
        required
      >
        <option value="">Select a farm</option>
        {farms.map(farm => (
          <option key={farm._id} value={farm._id}>{farm.name}</option>
        ))}
      </select>

      <button type="submit">Save Item</button>
    </form>
  );
};


const MenuManagement = () => {
    const [items, setItems] = useState([]);
    const [farms, setFarms] = useState([]);
    const [currentItem, setCurrentItem] = useState(null); // Used for edit operations
    const [currentFarm, setCurrentFarm] = useState(null); // Used for edit operations
    const [showItemPopup, setShowItemPopup] = useState(false);
    const [showFarmPopup, setShowFarmPopup] = useState(false);

    useEffect(() => {
        fetchItems();
        fetchFarms();
    }, []);

    const fetchItems = async () => {
        try {
            const response = await axios.get('/api/items');
            setItems(response.data);
        } catch (error) {
            console.error('Failed to fetch items:', error);
        }
    };

    const fetchFarms = async () => {
        try {
            const response = await axios.get('/api/farms');
            setFarms(response.data);
        } catch (error) {
            console.error('Failed to fetch farms:', error);
        }
    };

    const openItemPopup = (item = null) => {
        setCurrentItem(item); // null for add, object for edit
        setShowItemPopup(true);
    };

    const closeItemPopup = () => {
        setShowItemPopup(false);
        setCurrentItem(null); // Reset after closing
    };

    const openFarmPopup = (farm = null) => {
        setCurrentFarm(farm); // null for add, object for edit
        setShowFarmPopup(true);
    };

    const closeFarmPopup = () => {
        setShowFarmPopup(false);
        setCurrentFarm(null); // Reset after closing
    };

    return (
        <div className="menu-management-container">
            <button onClick={() => openItemPopup()} className="add-button">Add Menu Item</button>
            <button onClick={() => openFarmPopup()} className="add-button">Add Farm</button>
            <GenericPopup show={showItemPopup} onClose={closeItemPopup}>
                <ItemForm item={currentItem} onSave={() => {
                    fetchItems();
                    closeItemPopup();
                }} />
            </GenericPopup>

            <GenericPopup show={showFarmPopup} onClose={closeFarmPopup}>
                <FarmForm farm={currentFarm} onSave={() => {
                    fetchFarms();
                    closeFarmPopup();
                }} />
            </GenericPopup>

            <div className="menu-items-table">
                <h3>Menu Items</h3>
                <table>
                    <thead>
                        <tr>
                            <th>Item Name</th>
                            <th>Farm</th>
                            <th>Quantity Available</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {items.map(item => (
                            <tr key={item._id}>
                                <td>{item.itemName}</td>
                                <td>{item.farm ? item.farm.name : 'N/A'}</td>
                                <td>{item.quantityAvailable}</td>
                                <td>
                                    <button onClick={() => handleAddEditItem(item)} className="edit-btn">Edit</button>
                                    <button onClick={() => handleDeleteItem(item._id)} className="delete-btn">Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

        </div>
    );
};
export default MenuManagement;
