import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AllPages.css';

const BuildOrder = () => {
  const [orderData, setOrderData] = useState({
    customerName: '',
    customerEmail: '',
    deliveryAddress: '',
    deliveryDate: '',
    items: [],
  });
  const [availableItems, setAvailableItems] = useState([]);
  const [totalCost, setTotalCost] = useState(0);

  useEffect(() => {
    const fetchAvailableItems = async () => {
      try {
        const response = await axios.get('/api/items');
        setAvailableItems(response.data.map(item => ({
          ...item,
          quantity: 0,
          unitCost: item.unitCost || 0, // Assuming each item has a unitCost
        })));
      } catch (error) {
        console.error("Error fetching items:", error);
      }
    };

    fetchAvailableItems();
  }, []);

  const handleChange = (e) => {
    setOrderData({ ...orderData, [e.target.name]: e.target.value });
  };

  const handleItemChange = (index, value) => {
    let newItems = [...availableItems];
    newItems[index].quantity = Number(value);
    setAvailableItems(newItems);

    // Update total cost
    updateTotalCost(newItems);
  };

  const updateTotalCost = (items) => {
    const total = items.reduce((acc, item) => acc + (item.quantity * item.unitCost), 0);
    setTotalCost(total);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const order = {
      ...orderData,
      items: availableItems.filter(item => item.quantity > 0),
    };

    try {
      await axios.post('/api/orders', order);
      alert('Order submitted successfully!');
      // Redirect or clear form here as needed
    } catch (error) {
      console.error('Error submitting order', error);
      alert('Error submitting order. Please try again.');
    }
  };



  return (
    <div className="build-order-container">
      {/* Customer Information Section */}
      <div className="order-info-section">
        <h2>Customer Information</h2>
        {/* Input fields for customer information */}
      </div>

      {/* Build Cart Section */}
      <div className="build-cart-section">
        <h3>Build Your Cart</h3>
        {/* Listing items with input for quantity */}
        <ul className="item-list">
          {availableItems.map((item, index) => (
            <li key={index}>
              <div className="form-group">
                <label>{item.name} - ${item.unitCost.toFixed(2)} each:</label>
                <input type="number" min="0" value={item.quantity} onChange={(e) => handleItemChange(index, e.target.value)} />
              </div>
            </li>
          ))}
        </ul>
        <p className="total-cost">Total Cost: ${totalCost.toFixed(2)}</p>
      </div>

      {/* Submit Button */}
      <div className="submitButton">
        <button type="submit">Submit Order</button>
      </div>
    </div>
  );
};

export default BuildOrder;
