import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MenuManagement = () => {
    const [items, setItems] = useState([]);
    const [farms, setFarms] = useState([]);
    const [form, setForm] = useState({ itemName: '', quantityAvailable: '', farm: '' });
    const [farmForm, setFarmForm] = useState({ name: '', address: '', contactNumber: '' }); // New form for farm data
    const [isFarmModalOpen, setIsFarmModalOpen] = useState(false); // Modal control for adding/editing farms

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingId, setEditingId] = useState(null);

    useEffect(() => {
        fetchItems();
        fetchFarms(); // Fetch farms as well
    }, []);

    const fetchItems = async () => {
        try {
            const response = await axios.get('/api/items');
            setItems(response.data);
        } catch (error) {
            console.error('Failed to fetch items:', error);
        }
    };

    const fetchFarms = async () => {
        try {
            const response = await axios.get('/api/farms');
            console.log("Fetching Farms....")
            console.log(response.data); 
            setFarms(response.data); // Assuming the backend returns an array of farms
        } catch (error) {
            console.error('Failed to fetch farms:', error);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const action = editingId ? axios.put(`/api/items/${editingId}`, form) : axios.post('/api/items', form);
        try {
            await action;
            fetchItems();
            setIsModalOpen(false); // Close modal on success
            setForm({ itemName: '', quantityAvailable: '', farm: '' }); // Reset form
            setEditingId(null); // Reset editing state
        } catch (error) {
            console.error('Failed to process item:', error);
        }
    };

    const handleAddFarm = async () => {
    try {
        await axios.post('/api/farms', farmForm);
        fetchFarms(); // Refresh the list of farms
        setIsFarmModalOpen(false); // Close the modal
        setFarmForm({ name: '', address: '', contactNumber: '' }); // Reset the farm form
    } catch (error) {
        console.error('Failed to add farm:', error);
    }
};


    const openModal = () => {
        setIsModalOpen(true);
        setForm({ itemName: '', quantityAvailable: '', pickupAddress: '' }); // Reset form for new entry
        setEditingId(null); // Ensure we're not in editing mode
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    const startEdit = (item) => {
        setForm({ itemName: item.itemName, quantityAvailable: item.quantityAvailable, pickupAddress: item.pickupAddress });
        setEditingId(item._id);
        setIsModalOpen(true); // Open modal for editing
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`/api/items/${id}`);
            fetchItems();
        } catch (error) {
            console.error('Failed to delete item:', error);
        }
    };

return (
    <div>
        <h2>Menu Management</h2>
        <button onClick={() => setIsModalOpen(true)}>Add Menu Item</button>
        <button onClick={() => setIsFarmModalOpen(true)}>Add Farm</button>

        {isModalOpen && (
            <div className="modal">
                <div className="modal-content">
                    <span className="close" onClick={closeModal}>&times;</span>
                    <form onSubmit={handleSubmit}>
                        <input
                            type="text"
                            placeholder="Item Name"
                            value={form.itemName}
                            onChange={(e) => setForm({ ...form, itemName: e.target.value })}
                            required
                        />
                        <input
                            type="number"
                            placeholder="Quantity Available"
                            value={form.quantityAvailable}
                            onChange={(e) => setForm({ ...form, quantityAvailable: e.target.value })}
                            required
                        />
                        <select value={form.farm} onChange={(e) => setForm({ ...form, farm: e.target.value })}>
                            <option value="">Select a farm</option>
                            {farms.map(farm => (
                                <option key={farm._id} value={farm._id}>{farm.name}</option>
                            ))}
                        </select>
                        <button type="submit">{editingId ? 'Update Item' : 'Add Item'}</button>
                    </form>
                </div>
            </div>
        )}

        {isFarmModalOpen && (
            <div className="modal">
                <div className="modal-content">
                    <span className="close" onClick={() => setIsFarmModalOpen(false)}>&times;</span>
                    <form onSubmit={handleAddFarm}>
                        <input
                            type="text"
                            placeholder="Farm Name"
                            value={farmForm.name}
                            onChange={(e) => setFarmForm({ ...farmForm, name: e.target.value })}
                            required
                        />
                        <input
                            type="text"
                            placeholder="Address"
                            value={farmForm.address}
                            onChange={(e) => setFarmForm({ ...farmForm, address: e.target.value })}
                            required
                        />
                        <input
                            type="text"
                            placeholder="Contact Number"
                            value={farmForm.contactNumber}
                            onChange={(e) => setFarmForm({ ...farmForm, contactNumber: e.target.value })}
                        />
                        <button type="submit">Add Farm</button>
                    </form>
                </div>
            </div>
        )}

                <h3>Menu Items</h3>
            <table>
                    <thead>
                        <tr>
                            <th>Item Name</th>
                            <th>Farm</th>
                            <th>Quantity Available</th>
                        </tr>
                    </thead>
                <tbody>
                {items.map(item => (
                     <tr key={item._id}>
                        <td>{item.itemName}</td>
                        <td>{item.farm.name}</td> {/* Adjust based on your data structure */}
                        <td>{item.quantityAvailable}</td>
                    </tr>
                ))}
                </tbody>
            </table>
            <h3>Farms</h3>
            <table>
                    <thead>
                        <tr>
                            <th>Farm Name</th>
                        </tr>
                    </thead>
                <tbody>
                {farms.map(item => (
                     <tr key={farm._id}>
                        <td>{farm.name}</td>
                    </tr>
                ))}
                </tbody>
            </table>
            <ul>
                {items.map(item => (
                    <li key={item._id}>
                        {item.itemName} - {item.quantityAvailable} - {item.pickupAddress}
                        <button onClick={() => startEdit(item)}>Edit</button>
                        <button onClick={() => handleDelete(item._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default MenuManagement;
