import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { GenericTable, GenericPopup } from './ReusableReactComponents';
import './AllPages.css';

const MenuManagement = () => {
    const [items, setItems] = useState([]);
    const [farms, setFarms] = useState([]);
    const [form, setForm] = useState({ itemName: '', quantityAvailable: '', farm: '' });
    const [farmForm, setFarmForm] = useState({ name: '', address: '', contactNumber: '' });
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isFarmModalOpen, setIsFarmModalOpen] = useState(false);
    const [editMode, setEditMode] = useState(false); // New state to differentiate add vs edit mode

    useEffect(() => {
        fetchItems();
        fetchFarms();
    }, []);

    const fetchItems = async () => {
        try {
            const response = await axios.get('/api/items');
            setItems(response.data);
        } catch (error) {
            console.error('Failed to fetch items:', error);
        }
    };

    const fetchFarms = async () => {
        try {
            const response = await axios.get('/api/farms');
            setFarms(response.data);
        } catch (error) {
            console.error('Failed to fetch farms:', error);
        }
    };

    const handleSubmitItem = async (e) => {
        e.preventDefault();
        const endpoint = form._id ? `/api/items/${form._id}` : '/api/items';
        const action = form._id ? axios.put(endpoint, form) : axios.post(endpoint, form);
        try {
            await action;
            fetchItems();
            setIsModalOpen(false);
            resetForm();
        } catch (error) {
            console.error('Failed to save item:', error);
        }
    };

    const handleAddFarm = async (e) => {
        e.preventDefault();
        try {
            await axios.post('/api/farms', farmForm);
            fetchFarms();
            setIsFarmModalOpen(false);
            resetFarmForm();
        } catch (error) {
            console.error('Failed to add farm:', error);
        }
    };

    const resetForm = () => {
        setForm({ itemName: '', quantityAvailable: '', farm: '' });
    };

    const resetFarmForm = () => {
        setFarmForm({ name: '', address: '', contactNumber: '' });
    };

    const handleEditItem = (item) => {
        setForm(item);
        setIsModalOpen(true);
    };

    const handleDeleteItem = async (id) => {
        try {
            await axios.delete(`/api/items/${id}`);
            fetchItems();
        } catch (error) {
            console.error('Failed to delete item:', error);
        }
    };
    return (
        <div className="menu-management-container">
            <div className="control-panel">
                <button onClick={() => setIsModalOpen(true)} className="add-button">
                    <img src={`${process.env.PUBLIC_URL}/assets/logo128.png`} alt="AddItem" /> Add Menu Item
                </button>
                <button onClick={() => setIsFarmModalOpen(true)} className="add-button">
                    <img src="/path/to/farm-icon.png" alt="AddFarm" /> Add Farm
                </button>
            </div>
            <div className="menu-items-table">
                <h3>Menu Items</h3>
                <table>
                    <thead>
                        <tr>
                            <th>Item Name</th>
                            <th>Farm</th>
                            <th>Quantity Available</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {items.map(item => (
                            <tr key={item._id}>
                                <td>{item.itemName}</td>
                                <td>{item.farm ? item.farm.name : 'N/A'}</td>
                                <td>{item.quantityAvailable}</td>
                                <td>
                                    <button onClick={() => handleEditItem(item)}>Edit</button>
                                    <button onClick={() => handleDeleteItem(item._id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            {/* Modals for adding/editing menu items and farms go here */}
        </div>
    );
};
export default MenuManagement;
