import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MenuManagement = () => {
    const [items, setItems] = useState([]);
    const [form, setForm] = useState({ itemName: '', quantityAvailable: '', pickupAddress: '' });
    const [editingId, setEditingId] = useState(null);

    useEffect(() => {
        fetchItems();
    }, []);

    const fetchItems = async () => {
        try {
            const response = await axios.get('/api/items');
            setItems(response.data);
        } catch (error) {
            console.error('Failed to fetch items:', error);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if(editingId) {
            try {
                await axios.put(`/api/items/${editingId}`, form);
            } catch (error) {
                console.error('Failed to update item:', error);
            }
        } else {
            try {
                await axios.post('/api/items', form);
            } catch (error) {
                console.error('Failed to add new item:', error);
            }
        }
        setForm({ itemName: '', quantityAvailable: '', pickupAddress: '' });
        setEditingId(null);
        fetchItems();
    };

    const startEdit = (item) => {
        setForm({ itemName: item.itemName, quantityAvailable: item.quantityAvailable, pickupAddress: item.pickupAddress });
        setEditingId(item._id);
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`/api/items/${id}`);
            fetchItems();
        } catch (error) {
            console.error('Failed to delete item:', error);
        }
    };

    return (
        <div>
            <h2>Menu Management</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Item Name"
                    value={form.itemName}
                    onChange={(e) => setForm({ ...form, itemName: e.target.value })}
                    required
                />
                <input
                    type="number"
                    placeholder="Quantity Available"
                    value={form.quantityAvailable}
                    onChange={(e) => setForm({ ...form, quantityAvailable: e.target.value })}
                    required
                />
                <input
                    type="text"
                    placeholder="Pickup Address"
                    value={form.pickupAddress}
                    onChange={(e) => setForm({ ...form, pickupAddress: e.target.value })}
                />
                <button type="submit">{editingId ? 'Update Item' : 'Add Item'}</button>
            </form>
            <ul>
                {items.map(item => (
                    <li key={item._id}>
                        {item.itemName} - {item.quantityAvailable} - {item.pickupAddress}
                        <button onClick={() => startEdit(item)}>Edit</button>
                        <button onClick={() => handleDelete(item._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default MenuManagement;
