import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { GenericTable, GenericPopup } from './ReusableReactComponents';
import './AllPages.css';

const MenuManagement = () => {
    const [items, setItems] = useState([]);
    const [farms, setFarms] = useState([]);
    const [form, setForm] = useState({ itemName: '', quantityAvailable: '', farm: '' });
    const [farmForm, setFarmForm] = useState({ name: '', address: '', contactNumber: '' });
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isFarmModalOpen, setIsFarmModalOpen] = useState(false);

    useEffect(() => {
        fetchItems();
        fetchFarms();
    }, []);

    const fetchItems = async () => {
        try {
            const response = await axios.get('/api/items');
            setItems(response.data);
        } catch (error) {
            console.error('Failed to fetch items:', error);
        }
    };

    const fetchFarms = async () => {
        try {
            const response = await axios.get('/api/farms');
            setFarms(response.data);
        } catch (error) {
            console.error('Failed to fetch farms:', error);
        }
    };

    const handleSubmitItem = async (e) => {
        e.preventDefault();
        const endpoint = form._id ? `/api/items/${form._id}` : '/api/items';
        const action = form._id ? axios.put(endpoint, form) : axios.post(endpoint, form);
        try {
            await action;
            fetchItems();
            setIsModalOpen(false);
            resetForm();
        } catch (error) {
            console.error('Failed to save item:', error);
        }
    };

    const handleAddFarm = async (e) => {
        e.preventDefault();
        try {
            await axios.post('/api/farms', farmForm);
            fetchFarms();
            setIsFarmModalOpen(false);
            resetFarmForm();
        } catch (error) {
            console.error('Failed to add farm:', error);
        }
    };

    const resetForm = () => {
        setForm({ itemName: '', quantityAvailable: '', farm: '' });
    };

    const resetFarmForm = () => {
        setFarmForm({ name: '', address: '', contactNumber: '' });
    };

    const handleEditItem = (item) => {
        setForm(item);
        setIsModalOpen(true);
    };

    const handleDeleteItem = async (id) => {
        try {
            await axios.delete(`/api/items/${id}`);
            fetchItems();
        } catch (error) {
            console.error('Failed to delete item:', error);
        }
    };
return (
<div>
            <h2>Menu Management</h2>
            <button onClick={() => setIsModalOpen(true)}>Add Menu Item</button>
            <button onClick={() => setIsFarmModalOpen(true)}>Add Farm</button>

            {isModalOpen && (
                <div className="modal">
                    <div className="modal-content">
                        <span className="close" onClick={() => setIsModalOpen(false)}>&times;</span>
                        <form onSubmit={handleSubmitItem}>
                            {/* Form fields for item */}
                        </form>
                    </div>
                </div>
            )}

            {isFarmModalOpen && (
                <div className="modal">
                    <div className="modal-content">
                        <span className="close" onClick={() => setIsFarmModalOpen(false)}>&times;</span>
                        <form onSubmit={handleAddFarm}>
                <input
                    type="text"
                    placeholder="Farm Name"
                    value={farmForm.name}
                    onChange={(e) => setFarmForm({ ...farmForm, name: e.target.value })}
                    required
                />
                <input
                    type="text"
                    placeholder="Address"
                    value={farmForm.address}
                    onChange={(e) => setFarmForm({ ...farmForm, address: e.target.value })}
                    required
                />
                <input
                    type="text"
                    placeholder="Contact Number"
                    value={farmForm.contactNumber}
                    onChange={(e) => setFarmForm({ ...farmForm, contactNumber: e.target.value })}
                />
                <button type="submit">Add Farm</button>                        </form>
                    </div>
                </div>
            )}

                <h3>Menu Items</h3>
            <table>
                    <thead>
                        <tr>
                            <th>Item Name</th>
                            <th>Farm</th>
                            <th>Quantity Available</th>
                        </tr>
                    </thead>
                <tbody>
                {items.map(item => (
                     <tr key={item._id}>
                        <td>{item.itemName}</td>
                        <td>{item.farm.name}</td> {/* Adjust based on your data structure */}
                        <td>{item.quantityAvailable}</td>
                    </tr>
                ))}
                </tbody>
            </table>
            <h3>Farms</h3>
            <table>
                    <thead>
                        <tr>
                            <th>Farm Name</th>
                        </tr>
                    </thead>
                <tbody>
                {farms.map(item => (
                     <tr key={farm._id}>
                        <td>{farm.name}</td>
                    </tr>
                ))}
                </tbody>
            </table>
            <ul>
                {items.map(item => (
                    <li key={item._id}>
                        {item.itemName} - {item.quantityAvailable} - {item.pickupAddress}
                        <button onClick={() => startEdit(item)}>Edit</button>
                        <button onClick={() => handleDelete(item._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default MenuManagement;
